import React from 'react'

function Item({id},texto) {

const botonEditar = (<button onClick={handleClick}>Editar</button>)

function handleClick(){
    setTexto(10)
} 

  return (
    <div className='Grupo'>
    <div className='divInline'>
      {id}
    </div>
    <div className='divInline'>
      {botonEditar}
    </div>
    <div>
      <textarea rows="15" cols="30">{_texto}</textarea>
    </div>
    </div>
  )
}

export default Item
