import React from 'react';

// create a new component for the Item
class Item extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      text: this.props._item.texto
    };

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.props.appUpdateText(this.props._item.id, this.state.text);
  }

  dameID(id) {
    return 'areaTexto' + id;
  }

  render() {
    return (
      <div className="Grupo">
        <div className="divInline">{this.props._item.id}</div>
        <div className="divInline">
          <button onClick={this.handleClick}>Editar</button>
        </div>
        <div>
          <textarea
            id={this.dameID(this.props._item.id)}
            rows="15"
            cols="30"
            value={this.state.text}
            onChange={(event) => this.setState({ text: event.target.value })}
          ></textarea>
        </div>
      </div>
    );
  }
}

export default Item;
