import React from 'react';

// create a new functional component for the Item
const Item = (props) => {
  const botonEditar = (
    <button onClick={() => handleClick(props._item.id, props._item.texto)}>
      Editar
    </button>
  );

  const handleClick = (id, text) => {
    props.appUpdateText(id, text);
  };

  const dameID = (id) => {
    return 'areaTexto' + id;
  };

  return (
    <div className="Grupo">
      <div className="divInline">{props._item.id}</div>
      <div className="divInline">{botonEditar}</div>
      <div>
        <textarea
          id={dameID(props._item.id)}
          rows="15"
          cols="30"
          value={props._item.texto}
          onChange={(event) =>
            handleClick(props._item.id, event.target.value)
          }
        ></textarea>
      </div>
    </div>
  );
};

export default Item;
