import React from 'react'
import PropTypes from 'prop-types'
import {useState} from 'react'


function Header({appChangesText}) {

  let [_texto,setTexto ] = useState("")


function handleClick(id){
    appChangesText(id)
} 

  return (
    <div className='GrupoControl'>
        <div className='containerBotonesID'>
          <button onClick={() => handleClick("A")} className="divInline">A</button>
          <button onClick={() => handleClick("B")} className="divInline">B</button>
          <button onClick={() => handleClick("C")} className="divInline">C</button>
          <button onClick={() => handleClick("D")} className="divInline">D</button>
          <button onClick={() => handleClick("E")} className="divInline">E</button>
        </div> 
        <div>
          <textarea rows="15" cols="30" id='areaPrincipal'></textarea>
        </div>
        <div>
          <button disabled>Editar</button>
        </div>
    </div>
  )
}

Header.defaultProps ={
    text: 'Este es el texto por defecto si no recibo argumentos'
}
Header.propTypes = {
    text: PropTypes.string
}

export default Header
