import Header from "./components/Header"
import ListaItems from "./components/ListaItems"
import {useState} from 'react'


function App(){

    let info = [
        {"id":"A","texto":'a'},
        {"id":"B","texto":''},
        {"id":"C","texto":''},
        {"id":"D","texto":''},
        {"id":"E","texto":''}
    ]

    let [infoApp,updateInfo] = useState(info)

    


    function appChangesText(_id,_texto){
        let newElement
        return (
            newElement = {"id":_id,"texto":_texto},
            updateInfo((prev) => {
                prev[newElement.id].texto=prev[newElement.id].texto + '\n' +newElement.texto
                return prev
            }) 
            )
    }
console.log(infoApp)
    return (

        <>
        <div><h1>AGRUPADOR</h1></div>
        <Header appChangesText={appChangesText}/>
        <div className="divListaItems"> 
        <ListaItems _info={infoApp}/>
        </div>
        </>
    )


}
/*La funcion devuelve código JSX*/
/*importante: en JSX todos los elementos deben tener un PADRE */
/*className="container" class es una palabra reservada, en JSX se usa className*/
export  default App
