import React from 'react'
import {useState} from 'react'

function Item(id,texto) {

const botonEditar = (<button onClick={handleClick}>Editar</button>)

const [rating,setTexto ] = useState("defecto")

function handleClick(){
    setTexto(10)
} 

  return (
    <>
    <div className='item'>
        {rating}
    </div>
    {botonEditar}
    </>
  )
}

export default Item
