import React from 'react'
import {useState} from 'react'

function Item({id},texto) {

const botonEditar = (<button onClick={handleClick}>Editar</button>)

let [_texto,setTexto ] = useState("defecto")

function handleClick(){
    setTexto(10)
} 

  return (
    <div className='Grupo'>
    <div className='divInline'>
      {id}
    </div>
    <div className='divInline'>
      {botonEditar}
    </div>
    <div>
      <textarea rows="10">{_texto}.text</textarea>
    </div>
    </div>
  )
}

export default Item
